export default {
  name: "itemBankResourcePreview",
  props: {
    attachment: {
      type: Object,
      require: true
    }
  },
  inject: ["getResourceToken", "settings", "language"],
  data() {
    return {
      isLoaded: false,
      isShow: false,
      disableVideoIcon,
      icons,
      resourceStatus: {
        waiting: "等待转码",
        doing: "转码中...",
        error: "转码失败"
      },
      randomId: Math.floor(Math.random() * 100)
    };
  },
  computed: {
    isMedia() {
      return ["video", "audio"].indexOf(this.attachment.file_type) > -1;
    },
    iconSrc() {
      const { ext, file_type } = this.attachment;

      if (file_type === "other") return icons.other;

      if (file_type === "document") return icons[ext];

      return icons[file_type];
    },
    disableVideo() {
      let { isEncryptionPlus, securityVideoPlayer } = this.settings;

      if (!isEncryptionPlus) return false;

      if (securityVideoPlayer && !this.isWechat()) return true;

      if (!securityVideoPlayer) return true;

      return false;
    },
    playerId() {
      return "player" + this.attachment.id + "-" + this.randomId;
    }
  },
  methods: {
    isWechat() {
      const ua = navigator.userAgent.toLowerCase();
      if (ua.match(/MicroMessenger/i) == "micromessenger") {
        return true;
      } else {
        return false;
      }
    },
    async loadPlayer() {
      if (!this.getResourceToken || this.isLoaded) return;

      if (!window.QiQiuYun) {
        await loadScripts(
          `https://service-cdn.qiqiuyun.net/js-sdk-v2/sdk-v1.js?${Date.now()}`
        );
      }

      const initPlayer = data => {
        this.isLoaded = true;
        return new window.QiQiuYun.Player(Object.assign(data));
      };

      const res = await this.getResourceToken(this.attachment.global_id);

      res.data.id = this.playerId;
      res.data.language = this.language === "en" ? "en" : "zh-CN";
      initPlayer(res.data);
    },
    previewFile() {
      this.loadPlayer();

      if (!this.isMedia) {
        this.isShow = true;
      }
    }
  }
};
